"use client"

import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import axios from "axios"
import { toast } from "react-toastify"
import "react-toastify/dist/ReactToastify.css"
import {
  Button,
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  TextField,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  TextareaAutosize,
  Tabs,
  Tab,
  Box,
} from "@mui/material"
import { styled } from "@mui/system"

const StyledContainer = styled(Container)({
  marginTop: "2rem",
})

const StyledCard = styled(Card)({
  marginBottom: "1rem",
  backgroundColor: "#f5f5f5",
})

const StyledButton = styled(Button)({
  marginTop: "1rem",
})

const StyledTextArea = styled(TextareaAutosize)({
  width: "100%",
  marginTop: "0.5rem",
  marginBottom: "0.5rem",
  padding: "0.5rem",
  borderRadius: "4px",
  border: "1px solid #ccc",
})

function TabPanel(props) {
  const { children, value, index, ...other } = props

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  )
}

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  }
}

const FarmerDashboard = () => {
  const navigate = useNavigate()
  const [products, setProducts] = useState([])
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    price: "",
    category: "",
    quantity: "",
  })
  const [categories, setCategories] = useState([])
  const [isEditMode, setIsEditMode] = useState(false)
  const [editProductId, setEditProductId] = useState(null)
  const [educationalVideos, setEducationalVideos] = useState([])
  const [activeTab, setActiveTab] = useState("products")

  useEffect(() => {
    fetchProducts()
    fetchCategories()
    fetchEducationalVideos()
  }, [])

  useEffect(() => {
    let interval
    if (activeTab === "education") {
      interval = setInterval(() => {
        fetchEducationalVideos()
      }, 30000) // 30 seconds
    }
    return () => {
      if (interval) clearInterval(interval)
    }
  }, [activeTab, fetchEducationalVideos])

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue)
  }

  const fetchProducts = async () => {
    try {
      const token = localStorage.getItem("token")
      const response = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/products`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      setProducts(response.data)
    } catch (error) {
      console.error("Error fetching products:", error)
    }
  }

  const fetchCategories = async () => {
    try {
      const token = localStorage.getItem("token")
      const response = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/categories`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      setCategories(response.data)
    } catch (error) {
      console.error("Error fetching categories:", error)
    }
  }

  const fetchEducationalVideos = async () => {
    try {
      const token = localStorage.getItem("token")
      const response = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/education`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      setEducationalVideos(response.data)
    } catch (error) {
      console.error("Error fetching educational videos:", error)
    }
  }

  const handleInputChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value })
  }

  const handleAddProduct = async () => {
    try {
      const token = localStorage.getItem("token")
      await axios.post(`${process.env.REACT_APP_API_BASE_URL}/products`, newProduct, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      fetchProducts()
      setNewProduct({
        name: "",
        description: "",
        price: "",
        category: "",
        quantity: "",
      })
      toast.success("Product added successfully!")
    } catch (error) {
      console.error("Error adding product:", error)
      toast.error("Failed to add product.")
    }
  }

  const handleDeleteProduct = async (id) => {
    try {
      const token = localStorage.getItem("token")
      await axios.delete(`${process.env.REACT_APP_API_BASE_URL}/products/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      fetchProducts()
      toast.success("Product deleted successfully!")
    } catch (error) {
      console.error("Error deleting product:", error)
      toast.error("Failed to delete product.")
    }
  }

  const handleEditProduct = (product) => {
    setIsEditMode(true)
    setEditProductId(product._id)
    setNewProduct({
      name: product.name,
      description: product.description,
      price: product.price,
      category: product.category,
      quantity: product.quantity,
    })
  }

  const handleUpdateProduct = async () => {
    try {
      const token = localStorage.getItem("token")
      await axios.put(`${process.env.REACT_APP_API_BASE_URL}/products/${editProductId}`, newProduct, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      fetchProducts()
      setNewProduct({
        name: "",
        description: "",
        price: "",
        category: "",
        quantity: "",
      })
      setIsEditMode(false)
      setEditProductId(null)
      toast.success("Product updated successfully!")
    } catch (error) {
      console.error("Error updating product:", error)
      toast.error("Failed to update product.")
    }
  }

  const handleCancelEdit = () => {
    setIsEditMode(false)
    setEditProductId(null)
    setNewProduct({
      name: "",
      description: "",
      price: "",
      category: "",
      quantity: "",
    })
  }

  return (
    <StyledContainer maxWidth="md">
      <Typography variant="h4" component="h1" gutterBottom>
        Farmer Dashboard
      </Typography>

      <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
        <Tabs value={activeTab} onChange={handleTabChange} aria-label="basic tabs example">
          <Tab label="Products" value="products" {...a11yProps(0)} />
          <Tab label="Education" value="education" {...a11yProps(1)} />
        </Tabs>
      </Box>

      <TabPanel value={activeTab} index="products">
        <Typography variant="h6">Add New Product</Typography>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField fullWidth label="Name" name="name" value={newProduct.name} onChange={handleInputChange} />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField fullWidth label="Price" name="price" value={newProduct.price} onChange={handleInputChange} />
          </Grid>
          <Grid item xs={12}>
            <StyledTextArea
              minRows={3}
              placeholder="Description"
              name="description"
              value={newProduct.description}
              onChange={handleInputChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth>
              <InputLabel id="category-label">Category</InputLabel>
              <Select
                labelId="category-label"
                name="category"
                value={newProduct.category}
                label="Category"
                onChange={handleInputChange}
              >
                {categories.map((category) => (
                  <MenuItem key={category._id} value={category._id}>
                    {category.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Quantity"
              name="quantity"
              value={newProduct.quantity}
              onChange={handleInputChange}
            />
          </Grid>
        </Grid>
        <StyledButton variant="contained" color="primary" onClick={isEditMode ? handleUpdateProduct : handleAddProduct}>
          {isEditMode ? "Update Product" : "Add Product"}
        </StyledButton>
        {isEditMode && (
          <StyledButton variant="contained" color="secondary" onClick={handleCancelEdit}>
            Cancel Edit
          </StyledButton>
        )}

        <Typography variant="h6" style={{ marginTop: "2rem" }}>
          Your Products
        </Typography>
        {products.map((product) => (
          <StyledCard key={product._id}>
            <CardContent>
              <Typography variant="h5" component="h2">
                {product.name}
              </Typography>
              <Typography color="textSecondary">Category: {product.category}</Typography>
              <Typography variant="body2" component="p">
                {product.description}
              </Typography>
              <Typography variant="body2" component="p">
                Price: ${product.price}
              </Typography>
              <Typography variant="body2" component="p">
                Quantity: {product.quantity}
              </Typography>
              <StyledButton size="small" onClick={() => handleEditProduct(product)}>
                Edit
              </StyledButton>
              <StyledButton size="small" color="secondary" onClick={() => handleDeleteProduct(product._id)}>
                Delete
              </StyledButton>
            </CardContent>
          </StyledCard>
        ))}
      </TabPanel>

      <TabPanel value={activeTab} index="education">
        <Typography variant="h6">Educational Videos</Typography>
        <Grid container spacing={2}>
          {educationalVideos.map((video) => (
            <Grid item xs={12} sm={6} md={4} key={video._id}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{video.title}</Typography>
                  <Typography variant="body2">{video.description}</Typography>
                  <a href={video.url} target="_blank" rel="noopener noreferrer">
                    Watch Video
                  </a>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </TabPanel>
    </StyledContainer>
  )
}

export default FarmerDashboard
