"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import { useAuth } from "../context/UserContext"
import { Link } from "react-router-dom"
import DashboardFooter from "../components/DashboardFooter"
import "../EquipmentDashboard.css"

const EquipmentDashboard = () => {
  const { user, logout } = useAuth()
  const [equipment, setEquipment] = useState([])
  const [rentalRequests, setRentalRequests] = useState([])
  const [loading, setLoading] = useState(true)
  const [activeTab, setActiveTab] = useState("dashboard")
  const [newEquipment, setNewEquipment] = useState({
    name: "",
    equipment_type: "tractor",
    description: "",
    daily_rate: "",
    location: "",
    image: null,
  })

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch equipment seller's equipment
        const equipmentResponse = await axios.get("http://localhost:8000/api/equipment/my_equipment/")
        setEquipment(equipmentResponse.data)

        // Fetch rental requests for the equipment
        const requestsResponse = await axios.get("http://localhost:8000/api/equipment/rental_requests/")
        setRentalRequests(requestsResponse.data)

        setLoading(false)
      } catch (error) {
        console.error("Error fetching equipment data:", error)
        setLoading(false)
      }
    }

    fetchData()
  }, [])

  const handleLogout = () => {
    logout()
  }

  const handleEquipmentChange = (e) => {
    if (e.target.name === "image") {
      setNewEquipment({
        ...newEquipment,
        image: e.target.files[0],
      })
    } else {
      setNewEquipment({
        ...newEquipment,
        [e.target.name]: e.target.value,
      })
    }
  }

  const handleEquipmentSubmit = async (e) => {
    e.preventDefault()

    const formData = new FormData()
    Object.keys(newEquipment).forEach((key) => {
      formData.append(key, newEquipment[key])
    })

    try {
      const response = await axios.post("http://localhost:8000/api/equipment/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })

      setEquipment([...equipment, response.data])
      setNewEquipment({
        name: "",
        equipment_type: "tractor",
        description: "",
        daily_rate: "",
        location: "",
        image: null,
      })

      alert("Equipment added successfully!")
    } catch (error) {
      console.error("Error adding equipment:", error)
      alert("Failed to add equipment. Please try again.")
    }
  }

  const handleApproveRequest = async (requestId) => {
    try {
      await axios.post(`http://localhost:8000/api/equipment/rentals/${requestId}/approve/`)

      // Update the local state to reflect the change
      setRentalRequests(
        rentalRequests.map((request) => (request.id === requestId ? { ...request, status: "confirmed" } : request)),
      )

      alert("Rental request approved!")
    } catch (error) {
      console.error("Error approving request:", error)
      alert("Failed to approve request. Please try again.")
    }
  }

  const handleRejectRequest = async (requestId) => {
    try {
      await axios.post(`http://localhost:8000/api/equipment/rentals/${requestId}/reject/`)

      // Update the local state to reflect the change
      setRentalRequests(
        rentalRequests.map((request) => (request.id === requestId ? { ...request, status: "cancelled" } : request)),
      )

      alert("Rental request rejected!")
    } catch (error) {
      console.error("Error rejecting request:", error)
      alert("Failed to reject request. Please try again.")
    }
  }

  return (
    <div className="equipment-dashboard">
      <header className="dashboard-header">
        <Link to="/" className="dashboard-logo">
          <img src="/logo.png" alt="Smart Farm Connect" />
          <h1>Equipment Seller Dashboard</h1>
        </Link>
        <div className="user-info">
          <span>
            Welcome, {user.first_name} {user.last_name}
          </span>
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </header>

      <div className="dashboard-nav">
        <button className={activeTab === "dashboard" ? "active" : ""} onClick={() => setActiveTab("dashboard")}>
          Dashboard
        </button>
        <button className={activeTab === "equipment" ? "active" : ""} onClick={() => setActiveTab("equipment")}>
          My Equipment
        </button>
        <button className={activeTab === "requests" ? "active" : ""} onClick={() => setActiveTab("requests")}>
          Rental Requests
        </button>
      </div>

      <div className="dashboard-content">
        {loading ? (
          <p>Loading data...</p>
        ) : (
          <>
            {activeTab === "dashboard" && (
              <div className="dashboard-overview">
                <h2>Overview</h2>
                <div className="stats-grid">
                  <div className="stat-card">
                    <h3>My Equipment</h3>
                    <p className="stat-number">{equipment.length}</p>
                    <button onClick={() => setActiveTab("equipment")}>Manage Equipment</button>
                  </div>
                  <div className="stat-card">
                    <h3>Pending Requests</h3>
                    <p className="stat-number">{rentalRequests.filter((r) => r.status === "pending").length}</p>
                    <button onClick={() => setActiveTab("requests")}>View Requests</button>
                  </div>
                  <div className="stat-card">
                    <h3>Active Rentals</h3>
                    <p className="stat-number">{rentalRequests.filter((r) => r.status === "active").length}</p>
                    <button onClick={() => setActiveTab("requests")}>View Rentals</button>
                  </div>
                </div>

                <div className="recent-activity">
                  <h3>Recent Activity</h3>
                  <div className="activity-list">
                    {rentalRequests.slice(0, 5).map((request) => (
                      <div key={request.id} className="activity-item">
                        <p>
                          Rental request for {request.equipment_details?.name || "Unknown Equipment"} - Status:{" "}
                          <span className={`status-${request.status}`}>{request.status}</span>
                        </p>
                        <span className="activity-date">{new Date(request.created_at).toLocaleDateString()}</span>
                      </div>
                    ))}
                    {rentalRequests.length === 0 && <p>No recent activity.</p>}
                  </div>
                </div>
              </div>
            )}

            {activeTab === "equipment" && (
              <div className="equipment-section">
                <h2>My Equipment</h2>
                <div className="equipment-grid">
                  {equipment.length > 0 ? (
                    equipment.map((item) => (
                      <div key={item.id} className="equipment-card">
                        <div className="equipment-image">
                          {item.image ? (
                            <img src={item.image || "/placeholder.svg"} alt={item.name} />
                          ) : (
                            <img src="/placeholder.svg?height=150&width=200" alt="Equipment" />
                          )}
                        </div>
                        <div className="equipment-details">
                          <h3>{item.name}</h3>
                          <p>
                            <strong>Type:</strong> {item.equipment_type}
                          </p>
                          <p>
                            <strong>Daily Rate:</strong> ${item.daily_rate}
                          </p>
                          <p>
                            <strong>Location:</strong> {item.location}
                          </p>
                          <p>
                            <strong>Available:</strong> {item.is_available ? "Yes" : "No"}
                          </p>
                          <p>{item.description}</p>
                        </div>
                        <div className="equipment-actions">
                          <button className="edit-btn">Edit</button>
                          <button className="delete-btn">Delete</button>
                          <button className={`availability-btn ${item.is_available ? "available" : "unavailable"}`}>
                            {item.is_available ? "Mark Unavailable" : "Mark Available"}
                          </button>
                        </div>
                      </div>
                    ))
                  ) : (
                    <p>You haven't added any equipment yet.</p>
                  )}
                </div>

                <div className="add-equipment-form">
                  <h2>Add New Equipment</h2>
                  <form onSubmit={handleEquipmentSubmit}>
                    <div className="form-group">
                      <label>Equipment Name</label>
                      <input
                        type="text"
                        name="name"
                        value={newEquipment.name}
                        onChange={handleEquipmentChange}
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label>Equipment Type</label>
                      <select
                        name="equipment_type"
                        value={newEquipment.equipment_type}
                        onChange={handleEquipmentChange}
                        required
                      >
                        <option value="tractor">Tractor</option>
                        <option value="harvester">Harvester</option>
                        <option value="planter">Planter</option>
                        <option value="sprayer">Sprayer</option>
                        <option value="irrigation">Irrigation Equipment</option>
                        <option value="other">Other</option>
                      </select>
                    </div>

                    <div className="form-row">
                      <div className="form-group">
                        <label>Daily Rate ($)</label>
                        <input
                          type="number"
                          step="0.01"
                          name="daily_rate"
                          value={newEquipment.daily_rate}
                          onChange={handleEquipmentChange}
                          required
                        />
                      </div>

                      <div className="form-group">
                        <label>Location</label>
                        <input
                          type="text"
                          name="location"
                          value={newEquipment.location}
                          onChange={handleEquipmentChange}
                          required
                        />
                      </div>
                    </div>

                    <div className="form-group">
                      <label>Description</label>
                      <textarea
                        name="description"
                        value={newEquipment.description}
                        onChange={handleEquipmentChange}
                        rows="3"
                      ></textarea>
                    </div>

                    <div className="form-group">
                      <label>Equipment Image</label>
                      <input type="file" name="image" onChange={handleEquipmentChange} accept="image/*" />
                    </div>

                    <button type="submit" className="submit-btn">
                      Add Equipment
                    </button>
                  </form>
                </div>
              </div>
            )}

            {activeTab === "requests" && (
              <div className="requests-section">
                <h2>Rental Requests</h2>
                <div className="requests-list">
                  {rentalRequests.filter((r) => r.status === "pending").length > 0 ? (
                    rentalRequests
                      .filter((r) => r.status === "pending")
                      .map((request) => (
                        <div key={request.id} className="request-item">
                          <div className="request-info">
                            <h3>Request #{request.id}</h3>
                            <p>
                              <strong>Equipment:</strong> {request.equipment_details?.name || "Unknown"}
                            </p>
                            <p>
                              <strong>Renter:</strong> {request.renter_details?.username || "Unknown"}
                            </p>
                            <p>
                              <strong>Start Date:</strong> {new Date(request.start_date).toLocaleDateString()}
                            </p>
                            <p>
                              <strong>End Date:</strong> {new Date(request.end_date).toLocaleDateString()}
                            </p>
                            <p>
                              <strong>Total Cost:</strong> ${request.total_cost}
                            </p>
                          </div>
                          <div className="request-actions">
                            <button className="approve-btn" onClick={() => handleApproveRequest(request.id)}>
                              Approve
                            </button>
                            <button className="reject-btn" onClick={() => handleRejectRequest(request.id)}>
                              Reject
                            </button>
                          </div>
                        </div>
                      ))
                  ) : (
                    <p>No pending rental requests.</p>
                  )}
                </div>

                <h2>Active Rentals</h2>
                <div className="rentals-list">
                  {rentalRequests.filter((r) => r.status === "active" || r.status === "confirmed").length > 0 ? (
                    rentalRequests
                      .filter((r) => r.status === "active" || r.status === "confirmed")
                      .map((rental) => (
                        <div key={rental.id} className="rental-item">
                          <div className="rental-info">
                            <h3>Rental #{rental.id}</h3>
                            <p>
                              <strong>Equipment:</strong> {rental.equipment_details?.name || "Unknown"}
                            </p>
                            <p>
                              <strong>Renter:</strong> {rental.renter_details?.username || "Unknown"}
                            </p>
                            <p>
                              <strong>Start Date:</strong> {new Date(rental.start_date).toLocaleDateString()}
                            </p>
                            <p>
                              <strong>End Date:</strong> {new Date(rental.end_date).toLocaleDateString()}
                            </p>
                            <p>
                              <strong>Total Cost:</strong> ${rental.total_cost}
                            </p>
                            <p>
                              <strong>Status:</strong>{" "}
                              <span className={`status-${rental.status}`}>{rental.status}</span>
                            </p>
                          </div>
                          <div className="rental-actions">
                            {rental.status === "confirmed" && <button className="start-btn">Mark as Active</button>}
                            {rental.status === "active" && <button className="complete-btn">Complete Rental</button>}
                          </div>
                        </div>
                      ))
                  ) : (
                    <p>No active rentals.</p>
                  )}
                </div>

                <h2>Rental History</h2>
                <div className="history-list">
                  {rentalRequests.filter((r) => r.status === "completed" || r.status === "cancelled").length > 0 ? (
                    rentalRequests
                      .filter((r) => r.status === "completed" || r.status === "cancelled")
                      .map((rental) => (
                        <div key={rental.id} className="history-item">
                          <div className="history-info">
                            <h3>Rental #{rental.id}</h3>
                            <p>
                              <strong>Equipment:</strong> {rental.equipment_details?.name || "Unknown"}
                            </p>
                            <p>
                              <strong>Renter:</strong> {rental.renter_details?.username || "Unknown"}
                            </p>
                            <p>
                              <strong>Dates:</strong> {new Date(rental.start_date).toLocaleDateString()} to{" "}
                              {new Date(rental.end_date).toLocaleDateString()}
                            </p>
                            <p>
                              <strong>Total Cost:</strong> ${rental.total_cost}
                            </p>
                            <p>
                              <strong>Status:</strong>{" "}
                              <span className={`status-${rental.status}`}>{rental.status}</span>
                            </p>
                          </div>
                        </div>
                      ))
                  ) : (
                    <p>No rental history.</p>
                  )}
                </div>
              </div>
            )}
          </>
        )}
      </div>

      <DashboardFooter />
    </div>
  )
}

export default EquipmentDashboard
