"use client"

import { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useUser } from "../context/UserContext"

const LoginPage = () => {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  })
  const [error, setError] = useState("")
  const { login, loading } = useUser()
  const navigate = useNavigate()

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setError("")

    if (!formData.username || !formData.password) {
      setError("Please fill in all fields")
      return
    }

    const success = await login(formData.username, formData.password)
    if (success) {
      navigate("/dashboard")
    } else {
      setError("Invalid username or password")
    }
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        padding: "2rem",
      }}
    >
      <div
        style={{
          backgroundColor: "white",
          padding: "3rem",
          borderRadius: "20px",
          boxShadow: "0 20px 40px rgba(0,0,0,0.1)",
          width: "100%",
          maxWidth: "450px",
          border: "1px solid #f1f5f9",
        }}
      >
        {/* Header */}
        <div style={{ textAlign: "center", marginBottom: "2rem" }}>
          <Link to="/" style={{ textDecoration: "none" }}>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                gap: "0.5rem",
                marginBottom: "1rem",
              }}
            >
              <div
                style={{
                  width: "40px",
                  height: "40px",
                  background: "linear-gradient(135deg, #16a34a, #22c55e)",
                  borderRadius: "10px",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  fontSize: "1.5rem",
                }}
              >
                ðŸŒ±
              </div>
              <span
                style={{
                  fontWeight: "bold",
                  fontSize: "1.5rem",
                  background: "linear-gradient(135deg, #16a34a, #22c55e)",
                  WebkitBackgroundClip: "text",
                  WebkitTextFillColor: "transparent",
                }}
              >
                Smart Farm Connect
              </span>
            </div>
          </Link>
          <h2
            style={{
              fontSize: "2rem",
              color: "#1f2937",
              marginBottom: "0.5rem",
              fontWeight: "700",
            }}
          >
            Welcome back
          </h2>
          <p style={{ color: "#6b7280", fontSize: "1rem" }}>Sign in to your account to continue</p>
        </div>

        {/* Error Message */}
        {error && (
          <div
            style={{
              backgroundColor: "#fef2f2",
              border: "1px solid #fecaca",
              color: "#dc2626",
              padding: "0.75rem",
              borderRadius: "8px",
              marginBottom: "1.5rem",
              fontSize: "0.9rem",
            }}
          >
            {error}
          </div>
        )}

        {/* Form */}
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: "1.5rem" }}>
            <label
              style={{
                display: "block",
                marginBottom: "0.5rem",
                fontWeight: "600",
                color: "#374151",
              }}
            >
              Username
            </label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              style={{
                width: "100%",
                padding: "0.875rem",
                border: "2px solid #e5e7eb",
                borderRadius: "10px",
                fontSize: "1rem",
                transition: "all 0.2s",
                outline: "none",
              }}
              placeholder="Enter your username"
            />
          </div>

          <div style={{ marginBottom: "2rem" }}>
            <label
              style={{
                display: "block",
                marginBottom: "0.5rem",
                fontWeight: "600",
                color: "#374151",
              }}
            >
              Password
            </label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              style={{
                width: "100%",
                padding: "0.875rem",
                border: "2px solid #e5e7eb",
                borderRadius: "10px",
                fontSize: "1rem",
                transition: "all 0.2s",
                outline: "none",
              }}
              placeholder="Enter your password"
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            style={{
              width: "100%",
              background: loading ? "#9ca3af" : "linear-gradient(135deg, #16a34a, #22c55e)",
              color: "white",
              padding: "1rem",
              border: "none",
              borderRadius: "10px",
              fontSize: "1rem",
              fontWeight: "600",
              cursor: loading ? "not-allowed" : "pointer",
              transition: "all 0.3s",
              boxShadow: "0 4px 15px rgba(22, 163, 74, 0.3)",
            }}
          >
            {loading ? (
              <div style={{ display: "flex", alignItems: "center", justifyContent: "center", gap: "0.5rem" }}>
                <div
                  style={{
                    width: "20px",
                    height: "20px",
                    border: "2px solid transparent",
                    borderTop: "2px solid white",
                    borderRadius: "50%",
                    animation: "spin 1s linear infinite",
                  }}
                ></div>
                Signing in...
              </div>
            ) : (
              "Sign In"
            )}
          </button>
        </form>

        {/* Footer */}
        <div style={{ textAlign: "center", marginTop: "2rem" }}>
          <p style={{ color: "#6b7280", marginBottom: "1rem" }}>
            Don't have an account?{" "}
            <Link
              to="/signup"
              style={{
                color: "#16a34a",
                textDecoration: "none",
                fontWeight: "600",
              }}
            >
              Sign up here
            </Link>
          </p>

          <div
            style={{
              borderTop: "1px solid #e5e7eb",
              paddingTop: "1rem",
              fontSize: "0.9rem",
              color: "#9ca3af",
            }}
          >
            Demo credentials: username: <strong>farmer</strong>, password: <strong>password</strong>
          </div>
        </div>
      </div>
    </div>
  )
}

export default LoginPage
