"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import { useAuth } from "../context/UserContext"
import { Link } from "react-router-dom"
import DashboardFooter from "../components/DashboardFooter"
import "../BuyerDashboard.css"

const BuyerDashboard = () => {
  const { user, logout } = useAuth()
  const [products, setProducts] = useState([])
  const [orders, setOrders] = useState([])
  const [loading, setLoading] = useState(true)
  const [activeTab, setActiveTab] = useState("dashboard")
  const [searchTerm, setSearchTerm] = useState("")
  const [selectedCategory, setSelectedCategory] = useState("")

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch all available products
        const productsResponse = await axios.get("http://localhost:8000/api/products/")
        setProducts(productsResponse.data)

        // Fetch buyer's orders
        const ordersResponse = await axios.get("http://localhost:8000/api/products/my_orders/")
        setOrders(ordersResponse.data)

        setLoading(false)
      } catch (error) {
        console.error("Error fetching buyer data:", error)
        setLoading(false)
      }
    }

    fetchData()
  }, [])

  const handleLogout = () => {
    logout()
  }

  const filteredProducts = products.filter((product) => {
    const matchesSearch =
      product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      product.description.toLowerCase().includes(searchTerm.toLowerCase())
    const matchesCategory = selectedCategory ? product.category === selectedCategory : true
    return matchesSearch && matchesCategory && product.is_available
  })

  const categories = ["vegetables", "fruits", "grains", "livestock", "dairy", "other"]

  const handleOrderProduct = async (productId) => {
    try {
      // Navigate to product detail page or open a modal for order details
      window.location.href = `/product/${productId}`
    } catch (error) {
      console.error("Error ordering product:", error)
      alert("Failed to process order. Please try again.")
    }
  }

  return (
    <div className="buyer-dashboard">
      <header className="dashboard-header">
        <Link to="/" className="dashboard-logo">
          <img src="/logo.png" alt="Smart Farm Connect" />
          <h1>Buyer Dashboard</h1>
        </Link>
        <div className="user-info">
          <span>
            Welcome, {user.first_name} {user.last_name}
          </span>
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </header>

      <div className="dashboard-nav">
        <button className={activeTab === "dashboard" ? "active" : ""} onClick={() => setActiveTab("dashboard")}>
          Dashboard
        </button>
        <button className={activeTab === "marketplace" ? "active" : ""} onClick={() => setActiveTab("marketplace")}>
          Marketplace
        </button>
        <button className={activeTab === "orders" ? "active" : ""} onClick={() => setActiveTab("orders")}>
          My Orders
        </button>
      </div>

      <div className="dashboard-content">
        {loading ? (
          <p>Loading data...</p>
        ) : (
          <>
            {activeTab === "dashboard" && (
              <div className="dashboard-overview">
                <h2>Overview</h2>
                <div className="stats-grid">
                  <div className="stat-card">
                    <h3>Available Products</h3>
                    <p className="stat-number">{products.filter((p) => p.is_available).length}</p>
                    <button onClick={() => setActiveTab("marketplace")}>Browse Products</button>
                  </div>
                  <div className="stat-card">
                    <h3>My Orders</h3>
                    <p className="stat-number">{orders.length}</p>
                    <button onClick={() => setActiveTab("orders")}>View Orders</button>
                  </div>
                  <div className="stat-card">
                    <h3>Pending Orders</h3>
                    <p className="stat-number">{orders.filter((o) => o.status === "pending").length}</p>
                    <button onClick={() => setActiveTab("orders")}>View Pending</button>
                  </div>
                </div>

                <div className="recent-activity">
                  <h3>Recent Activity</h3>
                  <div className="activity-list">
                    {orders.slice(0, 5).map((order) => (
                      <div key={order.id} className="activity-item">
                        <p>
                          Order #{order.id} - {order.product_details?.name || "Unknown Product"} - Status:{" "}
                          <span className={`status-${order.status}`}>{order.status}</span>
                        </p>
                        <span className="activity-date">{new Date(order.created_at).toLocaleDateString()}</span>
                      </div>
                    ))}
                    {orders.length === 0 && <p>No recent activity.</p>}
                  </div>
                </div>
              </div>
            )}

            {activeTab === "marketplace" && (
              <div className="marketplace-section">
                <h2>Marketplace</h2>
                <div className="filter-container">
                  <input
                    type="text"
                    placeholder="Search products..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                  />

                  <select
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    className="category-select"
                  >
                    <option value="">All Categories</option>
                    {categories.map((category) => (
                      <option key={category} value={category}>
                        {category.charAt(0).toUpperCase() + category.slice(1)}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="products-grid">
                  {filteredProducts.length > 0 ? (
                    filteredProducts.map((product) => (
                      <div key={product.id} className="product-card">
                        <div className="product-image">
                          {product.image ? (
                            <img src={product.image || "/placeholder.svg"} alt={product.name} />
                          ) : (
                            <div className="placeholder-image">No Image</div>
                          )}
                        </div>
                        <div className="product-details">
                          <h3>{product.name}</h3>
                          <p className="product-category">{product.category}</p>
                          <p className="product-description">{product.description.substring(0, 100)}...</p>
                          <div className="product-meta">
                            <span className="product-price">
                              ${product.price} per {product.unit}
                            </span>
                            <span className="product-quantity">
                              Available: {product.quantity} {product.unit}
                            </span>
                          </div>
                          <p className="product-farmer">Seller: {product.farmer_details?.username || "Unknown"}</p>
                          <p className="product-location">Location: {product.location}</p>
                          <button className="order-btn" onClick={() => handleOrderProduct(product.id)}>
                            Order Now
                          </button>
                        </div>
                      </div>
                    ))
                  ) : (
                    <p>No products found matching your criteria.</p>
                  )}
                </div>
              </div>
            )}

            {activeTab === "orders" && (
              <div className="orders-section">
                <h2>My Orders</h2>
                <div className="orders-list">
                  {orders.length > 0 ? (
                    orders.map((order) => (
                      <div key={order.id} className="order-item">
                        <div className="order-info">
                          <h3>Order #{order.id}</h3>
                          <p>
                            <strong>Product:</strong> {order.product_details?.name || "Unknown"}
                          </p>
                          <p>
                            <strong>Quantity:</strong> {order.quantity} {order.product_details?.unit || "units"}
                          </p>
                          <p>
                            <strong>Total Price:</strong> ${order.total_price}
                          </p>
                          <p>
                            <strong>Seller:</strong> {order.product_details?.farmer_details?.username || "Unknown"}
                          </p>
                          <p>
                            <strong>Status:</strong> <span className={`status-${order.status}`}>{order.status}</span>
                          </p>
                          <p>
                            <strong>Order Date:</strong> {new Date(order.created_at).toLocaleDateString()}
                          </p>
                          <p>
                            <strong>Delivery Address:</strong> {order.delivery_address}
                          </p>
                        </div>
                        <div className="order-actions">
                          {order.status === "pending" && <button className="cancel-btn">Cancel Order</button>}
                          {order.status === "delivered" && <button className="review-btn">Leave Review</button>}
                        </div>
                      </div>
                    ))
                  ) : (
                    <p>You haven't placed any orders yet.</p>
                  )}
                </div>
              </div>
            )}
          </>
        )}
      </div>

      <DashboardFooter />
    </div>
  )
}

export default BuyerDashboard
